// ProductListPage.js

import React from 'react';
import { useParams, Link } from 'react-router-dom';
import productsData from '../data/products.json';

import './ProductList.css';


const ProductListPage = ({ categoryId }) => {
  const filteredProducts = productsData.filter(product => product.categoryId === categoryId);


  return (
    <div className='product-list'>
      <h2>Products</h2>
      <h2>$(categoryId)</h2>
      <ul>
        {filteredProducts.map(product => (
            <li key={product.id} className="product-item">
                <Link to={`/product/${product.id}`}> {/* Изменяем ссылку для перехода на страницу с деталями продукта */}
                  <h3>{product.name}</h3>
                  <img src={require(`../images/${product.image}`)} alt={product.name} />
                  <p>{product.price} сом</p>
                </Link>
            </li>
          ))}
      </ul>
    </div>
  );
};

export default ProductListPage;
