// ProductListPage.js

import React from 'react';
import { useParams } from 'react-router-dom';
import productsData from '../data/products.json';
import './ProductList.css';


const ProductListPage = () => {
  const { categoryId } = useParams(); // Получаем параметр categoryId из URL
  const filteredProducts = productsData.filter(product => product.categoryId === categoryId);

  return (
    <div className='product-list'>
      <h2>Products</h2>
      {productsData.map(product => (
          <li key={product.id} className="prod-item">
              <img src={require(`../images/${product.image}`)} alt={product.name} />
              <span>{product.name}</span>
          </li>
        ))}
    </div>
  );
};

export default ProductListPage;
